// Table: hansards_document
export const hansardsDocument = parliamentSchema.table(
  "hansards_document",
  {
    id: serial("id").primaryKey(),
    date: date("date", { mode: "date" }),
    number: varchar("number", { length: 6 }).notNull().default(""),
    sessionId: varchar("session_id", { length: 4 })
      .notNull()
      .references(() => coreSession.id),
    mostFrequentWord: varchar("most_frequent_word", { length: 20 })
      .notNull()
      .default(""),
    wordcloud: varchar("wordcloud", { length: 100 }),
    documentType: varchar("document_type", { length: 1 }).notNull(),
    sourceId: integer("source_id").notNull().unique(),
    downloaded: boolean("downloaded").notNull(),
    public_: boolean("public").notNull(),
    multilingual: boolean("multilingual").notNull(),
    skipParsing: boolean("skip_parsing").notNull(),
    xmlSourceUrl: varchar("xml_source_url", { length: 200 })
      .notNull()
      .default(""),
    firstImported: timestamp("first_imported", {
      withTimezone: true,
      precision: 6,
    }),
    lastImported: timestamp("last_imported", {
      withTimezone: true,
      precision: 6,
    }),
    skipRedownload: boolean("skip_redownload").notNull(),
  },
  (table) => {
    return {
      hansardsDocumentDocumentType: index("hansards_document_document_type").on(
        table.documentType
      ),
      hansardsDocumentDocumentTypeLike: index(
        "hansards_document_document_type_like"
      ).on(table.documentType),
      hansardsDocumentSourceId: index("hansards_document_source_id").on(
        table.sourceId
      ),
      hansardsHansardSessionId: index("hansards_hansard_session_id").on(
        table.sessionId
      ),
    };
  }
);

// Table: hansards_statement
export const hansardsStatement = parliamentSchema.table(
  "hansards_statement",
  {
    id: serial("id").primaryKey(),
    documentId: integer("document_id")
      .notNull()
      .references(() => hansardsDocument.id),
    time: timestamp("time", { withTimezone: true, precision: 6 }).notNull(),
    h1En: varchar("h1_en", { length: 300 }).notNull().default(""),
    h2En: varchar("h2_en", { length: 300 }).notNull().default(""),
    memberId: integer("member_id").references(() => coreElectedmember.id),
    whoEn: varchar("who_en", { length: 300 }).notNull().default(""),
    contentEn: text("content_en").notNull(),
    sequence: integer("sequence").notNull(),
    wordcount: integer("wordcount").notNull(),
    politicianId: integer("politician_id").references(() => corePolitician.id),
    procedural: boolean("procedural").notNull(),
    h3En: varchar("h3_en", { length: 300 }).notNull().default(""),
    whoHocid: integer("who_hocid"),
    contentFr: text("content_fr").notNull().default(""),
    statementType: varchar("statement_type", { length: 35 })
      .notNull()
      .default(""),
    writtenQuestion: varchar("written_question", { length: 1 })
      .notNull()
      .default(""),
    sourceId: varchar("source_id", { length: 15 }).notNull().default(""),
    whoContextEn: varchar("who_context_en", { length: 300 })
      .notNull()
      .default(""),
    slug: varchar("slug", { length: 100 }).notNull().default(""),
    urlcache: varchar("urlcache", { length: 200 }).notNull().default(""),
    h1Fr: varchar("h1_fr", { length: 400 }).notNull().default(""),
    h2Fr: varchar("h2_fr", { length: 400 }).notNull().default(""),
    h3Fr: varchar("h3_fr", { length: 400 }).notNull().default(""),
    whoFr: varchar("who_fr", { length: 500 }).notNull().default(""),
    whoContextFr: varchar("who_context_fr", { length: 500 })
      .notNull()
      .default(""),
    wordcountEn: smallint("wordcount_en"),
    billDebateStage: varchar("bill_debate_stage", { length: 10 })
      .notNull()
      .default(""),
    billDebatedId: integer("bill_debated_id").references(() => billsBill.id),
  },
  (table) => {
    return {
      hansardsStatementDocumentIdUniq: unique(
        "hansards_statement_document_id_77a67b806d7aef3_uniq"
      ).on(table.documentId, table.slug),
      hansardsStatementWhoHocidCheck: check(
        "hansards_statement_who_hocid_check",
        sql`(who_hocid >= 0)`
      ),
      hansardsStatementWordcountEnCheck: check(
        "hansards_statement_wordcount_en_check",
        sql`(wordcount_en >= 0)`
      ),
      hansardsStatementBillDebateStage: index(
        "hansards_statement_bill_debate_stage_39688425"
      ).on(table.billDebateStage),
      hansardsStatementBillDebateStageLike: index(
        "hansards_statement_bill_debate_stage_39688425_like"
      ).on(table.billDebateStage),
      hansardsStatementBillDebatedId: index(
        "hansards_statement_bill_debated_id_e9c56f18"
      ).on(table.billDebatedId),
      hansardsStatementHansardId: index("hansards_statement_hansard_id").on(
        table.documentId
      ),
      hansardsStatementMemberId: index("hansards_statement_member_id").on(
        table.memberId
      ),
      hansardsStatementPoliticianId: index(
        "hansards_statement_politician_id"
      ).on(table.politicianId),
      hansardsStatementPoliticianOrdered: index(
        "hansards_statement_politician_ordered"
      ).on(table.politicianId, table.time),
      hansardsStatementSequence: index("hansards_statement_sequence").on(
        table.sequence
      ),
      hansardsStatementSlug: index("hansards_statement_slug").on(table.slug),
      hansardsStatementSlugLike: index("hansards_statement_slug_like").on(
        table.slug
      ),
      hansardsStatementSpeaker: index("hansards_statement_speaker").on(
        table.procedural
      ),
      hansardsStatementTime: index("hansards_statement_time").on(table.time),
      hansardsStatementWhoHocid: index("hansards_statement_who_hocid").on(
        table.whoHocid
      ),
    };
  }
);

